/* Issues */

@import url('https://fonts.googleapis.com/css?family=Fira+Mono');

div.issue {
    background: #373b41;
}
table.list tr.issue a {
    color: #8abeb7;
}
img.gravatar {
    background: #282a2e;
    border-color: #282a2e;
}
div.flash.notice, #flash_notice {
    background: #cc6666;
}
div.wiki pre {
    background-color: #f9d1d1;
    border-color: rgba(222, 36, 59, 0.78);
    border-radius: 5px;
    border-width: 2px;
    border-style: dashed;
    font-family: 'Fira Mono', monospace;
}
ul.tagit {
    background: #373b41;
}
/* Issue Table */

.mypage-box {
   color: #ffbbbb;
}
div#issue-changesets{
	background: #628db6;
}
div#issue-changesets div.changeset{
	border-bottom: 1px solid #628db6;
}
.odd {
	background-color: #4e7294;
}
.even {
	background-color:#628db6;
}
table.list th {
    background-color: #416136;
    color: #fff574;
}
table.list thead th {
    border-top-color: #b4b7b4;
    border-right-color: #b4b7b4;
    border-left-color: #b4b7b4;
    border-bottom-color: #b4b7b4;
}
table tr.odd {
    background-color: #282a2e;
}
table tr.even,
table tr.even.priority-lowest,
tr.odd.priority-lowest {
    background-color: #373b41;
}
table tr.even.priority-lowest {
    color: #b294bb;
}
table tr.odd.priority-lowest {
    color: #b294bb;
}
table.list tbody tr:hover {
    background-color: #cc6666;
    color: #ffffff;
}
table.list tbody tr:hover a {
    color: #c5c8c6;
}
table.list tbody td,
table.list tbody tr:hover td {
    border: solid 1px #b4b7b4;
}
tr.odd.priority-lowest td,
tr.even.priority-lowest td,
tr.even.priority-lowest td,
tr.even.priority-lowest td {
    border-color: #b4b7b4;
}
div#content .context-menu-selection {
    background-color: #a3685a !important;
}
select[multiple="multiple"] {
    background-color: #373b41;
    color: #e0e0e0;
}
#ajax-modal p.buttons input:hover,
.ui-datepicker .ui-datepicker-buttonpane button.ui-state-hover,
input[type="submit"][name="continue"]:hover,
input[type="button"]:hover,
.details input[type="submit"]:hover,
input[type="submit"].small:hover,
input[type="submit"].button-small:hover {
    background: #1d1f21;
    color: #ffffff;
}
#ajax-modal p.buttons input,
.ui-datepicker .ui-datepicker-buttonpane button,
.details input[type="submit"],
input[type="button"],
input[type="submit"],
input[type="submit"][name="continue"],
input[type="submit"].small,
input[type="submit"].button-small {
    background: #373b41;
}
/* Body Text */

body {
    color: #e0e0e0;
}
/* Header */

#content h1,
h2,
h3,
h4 {
    color: #ffffff;
}
input[type="text"],
input[type="password"],
fieldset#filters td.values input {
    background-color: #373b41;
    border: 1px solid #969896;
    color: #e0e0e0;
}
input[type="text"]:focus,
input[type="password"]:focus,
select:focus,
textarea:focus,
fieldset#filters td.values input:focus {
    border-color: #81a2be;
}
select {
    background-color: #373b41;
    background: none;
    color: #e0e0e0;
    border: 1px solid #969896;
}
select option {
    background-color: #373b41;
    color: #e0e0e0;
}
/* Main Background */

html {
    background-color: #282a2e;
}
#content {
    background-color: #282a2e;
    border-right: none;
}
a,
a:link,
a:visited {
    color: #de935f;
}
#footer {
    background-color: #282a2e;
    border-top: none;
}
#top-menu a {
    color: #8abeb7;
}
p.other-formats {
    color: #969896;
}
/* Main Header */

#header {
    background-color: #373b41;
    color: #c5c8c6;
}
#main-menu li > a {
    color: #b5bd68;
}
/* Sidebar */

#main {
    background-color: #282a2e;
}
#sidebar h3 {
    color: #e0e0e0;
}
#sidebar a.selected {
    background-color: #373b41;
    color: #f0c674;
}
/* Calendar */

table.cal thead th,
table.cal td.week-number {
    background-color: #373b41;
}
table td.odd {
    background-color: #282a2e;
}
table td.even {
    background-color: #373b41;
    background: none;
}
table.cal td.today {
    background-color: #de935f;
}
div.tooltip:hover span.tip {
    background-color: #373b41;
    color: #e0e0e0;
}
table.cal td {
    border-color: #b4b7b4;
}
table.cal {
    border-color: #b4b7b4;
}
/* Wiki */

.wiki h1,
.wiki h2,
.wiki h3 {
    color: #ffffff;
}
.wiki.wiki-page {
    color: #e0e0e0;
}
/* Editing */

.box {
    background: #373b41;
    color: #e0e0e0;
}
textarea {
    background: #1d1f21;
    color: #e0e0e0;
}
input[type="text"]:focus,
input[type="password"]:focus,
select:focus,
textarea:focus,
fieldset#filters td.values input:focus {
    border-color: #81a2be;
}
#ajax-modal p.buttons input[type="submit"]:hover,
.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-close.ui-state-hover,
input[type="submit"]:hover {
    background: #8abeb7;
}
#ajax-modal p.buttons input[type="submit"],
.ui-datepicker .ui-datepicker-buttonpane button.ui-datepicker-close,
input[type="submit"] {
    background: #81a2be;
}
ul.tagit {
    background: #282a2e !important;
}
ul.tagit li.tagit-new input {
    background: #373b41 !important;
}
#attachments_fields input.filename {
    color: #e0e0e0;
}
#preview fieldset {
  background: inherit;
}

/* Settings */

#content .tabs ul li {
    background: #282a2e;
}
#content .tabs ul li a.selected {
    background-color: #b294bb;
    color: #ffffff;
}
#content .tabs ul li a.selected:hover,
#content .tabs ul li a:hover {
    background-color: #1d1f21;
    color: #ffffff;
}
#content .tabs ul li a {
    background-color: #282a2e;
    color: #e0e0e0;
}
legend {
    color: #c5c8c6;
}
.nodata,
.warning {
    background-color: #de935f;
    border-color: #de935f;
    color: #ffffff;
}
/* Date Picker */

.ui-widget-content,
.ui-datepicker .ui-state-default {
    background: #373b41;
    border-color: #969896;
    color: #e0e0e0;
}
.ui-datepicker .ui-state-default:hover {
    background: #1d1f21;
    color: #ffffff;
}
.ui-datepicker .ui-widget-header {
    background: #282a2e;
    border-color: #969896;
}
.ui-state-hover,
.ui-widget-content .ui-state-hover,
.ui-widget-header .ui-state-hover,
.ui-state-focus,
.ui-widget-content .ui-state-focus,
.ui-widget-header .ui-state-focus {
    background: #1d1f21;
    color: #ffffff;
}
/* Activity */

div#activity dt .time {
    color: #c5c8c6;
}
div#activity dd span.description,
#search-results dd span.description {
    color: #e0e0e0;
}
pre {
	color: #461e1e;
}

div#preview pre {
	color: white !important;
}

/*! Github Dark Blues Theme v1.5.0 (04-Oct-2016) */
/*
   * https://github.com/doublehelix/Stylish-Themes
   * http://userstyles.org/styles/120866
   * License: http://creativecommons.org/licenses/by/4.0/
   */

html,
body {
    color: #eee;
    background-color: rgba(68, 85, 102, 0.85);
}
.container {
    width: 80%;
    min-width: 980px;
}
.dashboard .news {
    background: rgba(68, 85, 102, 0.85);
    border: 1px solid #ccc;
    border-radius: 0.3em;
    padding-top: 0.6em;
    padding-bottom: 1em;
}
.warning {
    background-color: #fffccc;
    color: #900;
}
.warning table.files td.icon {
    color: #900;
}
.header,
.site-header {
    background-color: rgba(30, 48, 62, 0.9);
    border-bottom: 1px solid #E5E5E5;
    color: #ddd;
}
.site-footer {
    color: #ccc;
}
.protip {
    margin-top: 20px;
    margin-bottom: 20px;
    text-align: center;
    border: 1px solid #789;
    background: rgba(17, 34, 51, 0.8);
    border-radius: 4px;
    padding: 0.5em 1em;
}
.repohead h1 {
    color: #eee;
}
.header-logo-wordmark {
    color: #fff;
}
.header-logo-wordmark:hover {
    color: #40bdf7;
}
.header-logo-invertocat {
    color: #fff;
}
.header-logo-invertocat:hover {
    color: #40bdf7;
}
.header-nav-link {
    color: #fff;
}
.header-nav-link:hover {
    color: #40bdf7;
}
.auth-form-header {
    color: #FFF;
    text-shadow: 0px -1px 0px rgba(0, 0, 0, 0.3);
    background-color: rgba(17, 34, 51, 0.8);
    border: 1px solid #789;
    border-radius: 5px 5px 0px 0px;
}
.auth-form-body {
    background-color: rgba(30, 48, 62, 0.9);
    border-width: 0px 1px 1px;
    border-style: none solid solid;
    border-color: -moz-use-text-color #789 #789;
    border-image: none;
    border-radius: 0px 0px 5px 5px;
}
.session-authentication {
    background-color: rgba(68, 85, 102, 0.85);
}
.session-authentication .auth-form-header {
    background-color: transparent;
    color: #eee;
}
.session-authentication .header-logo {
    color: #fff;
}
.header-search-wrapper {
    padding: 0;
}
.url-field {
    min-height: 26px;
}
/** Unread notifications indicator **/

.notification-indicator .mail-status {
    background-image: linear-gradient(#f0a6f4, #40bdf7);
    color: #fff;
    border: 2px solid #E5E5E5;
}
.main-content {
    padding-bottom: 24px;
}
.discussion-sidebar {
    padding: 0.8em;
    border: 1px solid #789;
    margin-right: -30px;
    border-radius: 0.4em;
    box-shadow: 0.2em 0.2em 0.5em rgba(0, 0, 0, 0.5);
    box-sizing: border-box;
    background: rgba(17, 34, 51, 0.8);
    width: 220px;
}
.discussion-timeline-actions {
    border-top: 2px solid transparent;
    background: transparent;
}
.thread-subscription-status {
    color: #fff;
    background: transparent;
    border: 1px solid #E5E5E5;
}
.progress-bar {
    background-color: #678;
}
.sunken-menu {
    background-image: linear-gradient(to right, rgba(30, 48, 62, 0.9) 0%, rgba(68, 85, 102, 0.85) 8px);
    box-shadow: 1px 0px 0px #456 inset;
}
.sunken-menu::before {
    background-color: transparent;
    background-image: linear-gradient(rgba(68, 85, 102, 0.85), rgba(255, 255, 255, 0));
}
.sunken-menu::after {
    background-color: transparent;
    background-image: linear-gradient(rgba(255, 255, 255, 0), rgba(68, 85, 102, 0.85));
}
.sunken-menu-item .octicon {
    color: #ccc;
}
.sunken-menu-item:focus .octicon,
.sunken-menu-item:hover .octicon {
    color: #fff;
}
.sunken-menu-item.selected {
    color: #fff;
    background-color: rgba(68, 85, 102, 0.85);
    border-color: #678 #0c0 #678 transparent;
    border-radius: 0px 4px 4px 0px;
    box-shadow: 3px 1px 3px rgba(0, 0, 0, 0.35);
}
.sunken-menu-item.selected .octicon {
    color: #fff;
}
.sunken-menu-item.selected:after {
    width: 3px;
    background-color: #0c0;
    border-radius: 0 3px 3px 0;
}
.sunken-menu-separator {
    background-image: radial-gradient(farthest-side at left top, #345, rgba(244, 244, 244, 0));
}
.sunken-menu-separator::before {
    background-image: linear-gradient(to right, #678 70%, #89a 100%);
}
.tabnav-pr .tabnav-tab,
.tabnav-tab {
    color: #ddd;
    background-color: rgba(44, 61, 78, 0.8);
    border: 1px solid #789;
    border-bottom: none;
    margin-bottom: 1px;
    border-radius: 3px 3px 0 0;
}
.tabnav-tab.selected {
    background-color: rgba(119, 136, 153, 0.8);
}
.tabnav-pr .tabnav-tab.selected {
    color: #fff;
}
.tabnav-extra {
    color: #ddd;
}
a.tabnav-extra:hover {
    color: #40bdf7;
}
.subnav-item {
    border: 1px solid #789;
    background-color: rgba(44, 61, 78, 0.8);
    color: #ccc;
}
.subnav-item:hover,
.subnav-item:focus {
    text-decoration: none;
    background-color: #446cc6;
    color: #eee;
}
.subnav-item.selected,
.subnav-item.selected:hover,
.subnav-item.selected:focus {
    color: #fff;
    background-color: #446cc6;
    border-color: #f0a6f4;
}
.subnav-search-context .btn {
    color: #fff;
}
.text-emphasized {
    color: #fff;
}
.text-muted,
.text-gray {
    color: #ccc !important;
}
.table-list {
    background-color: rgba(44, 61, 78, 0.8);
}
.repository-meta {
    color: #eee;
}
.repo-list-item {
    background: rgba(68, 85, 102, 0.85);
    border: 1px solid #ccc;
    border-radius: 0.4em;
    padding: 1em 1em 4em 1em;
    margin: 1em -1em;
}
.repo-list-item .repo-list-stats,
.repo-list-item .repo-list-name,
.repo-list-item .repo-list-description,
.repo-list-item .repo-list-meta {
    z-index: 2;
    position: relative;
}
.repo-list .participation-graph {
    left: 1em;
    z-index: 1;
    color: #f0a6f4;
    opacity: 0.2 !important;
}
.repo-list .participation-graph .bars rect {
    fill: #f0a6f4 !important;
}
.private .mini-repo-list-item {
    background-color: rgba(44, 61, 78, 0.8);
}
.mini-repo-list-item .repo-description,
.mini-repo-list-item .repo-icon,
.mini-repo-list-item .stars {
    color: #eee;
    fill: currentColor;
}
.filter-bar {
    background-color: rgba(44, 61, 78, 0.8);
    border-bottom: 1px solid #E5E5E5;
}
.repo-filterer .repo-filter.filter-selected {
    color: #fff;
    border-bottom-color: #40bdf7;
}
.feature-callout {
    color: #666;
    background-color: rgba(17, 34, 51, 0.8);
    border: 1px solid #e9e9e9;
    border-top: 1px dashed #ddd;
    border-bottom: 0;
}
.feature-callout .feature-dismiss-link {
    color: #40bdf7;
}
/**************************************************/
/* Home Page */
/**************************************************/

.bootcamp h1 {
    background-color: rgba(30, 48, 62, 0.9);
    border-bottom: none;
}
.bootcamp .bootcamp-body {
    background-color: rgba(44, 61, 78, 0.8);
    /* border-color: #345 #678 #678 #678; */

    border-color: #678;
}
.bootcamp ul li {
    background-image: linear-gradient(#fff, #999);
    border: 1px solid #678;
    box-shadow: 0 0 8px rgba(0, 0, 0, 0.3);
}
input.input-contrast,
.input-contrast {
    background-color: #234;
    color: #fff;
    border-color: #678;
}
input.input-contrast:focus,
.input-contrast:focus {
    background-color: #345;
    color: #fff;
}
.site-header-nav-main .nav-item {
    border-top: 0;
    border-bottom: 4px solid transparent;
}
.site-header-nav .nav-item {
    color: #ccc;
}
.site-header-nav .nav-item:hover {
    color: #fff;
}
.pagehead-nav-item {
    color: #ccc;
}
.pagehead-nav-item:hover {
    color: #fff;
}
.pagehead-nav-item.selected {
    color: #fff;
    border-bottom: 3px solid #446cc6;
}
/* Explore GitHub */

.collection-card {
    background: #345;
    border: 1px solid #678;
    border-radius: 3px;
    box-shadow: 0 0 5px rgba(0, 0, 0, 0.3);
}
.collection-card-meta {
    color: #ccc;
}
/**************************************************/
/* STATES */
/**************************************************/

.state {
    background-color: #999;
}
.state-open,
.state-proposed,
.state-reopened {
    background-color: #6cc644;
    color: #fff;
}
.state-closed {
    background-color: #DD2C00;
}
.state-merged {
    background-color: #6E5494;
    border: 1px solid #9e84c4;
}
.orghead {
    background-color: rgba(17, 34, 51, 0.8);
    color: #ccc;
}
.org-link {
    color: #ccc;
}
.org-link:hover {
    color: #40bdf7;
}
.member-username {
    color: #eee;
}
.member-list-item .member-link:hover .member-username {
    color: #40bdf7;
}
.member-info .member-fullname {
    color: #ccc;
}
.team-grid .team {
    background: rgba(30, 48, 62, 0.9);
}
.team-grid .team-name {
    color: #fff;
}
.team-grid .team-members {
    background-color: rgba(68, 85, 102, 0.85);
}
.feature .intro {
    color: #ccc;
}
.full-width-divider {
    background: rgba(68, 85, 102, 0.85);
    border-top: 1px solid #ddd;
}
.sub-nav > h2,
.sub-nav > h2 > a {
    color: #ccc;
}
.sub-nav li .active {
    color: #fff;
    border-bottom: 2px solid #d8d8d8;
}
.sub-nav li a {
    color: #ccc;
}
.sub-nav li a:hover {
    color: #40bdf7;
}
.sidebar-module h3 a {
    color: #eee;
}
.sidebar-module h3 a:hover {
    color: #40bdf7;
    background-color: rgba(44, 61, 78, 0.8);
}
.sidebar-module ul ul li {
    border-bottom: 1px solid #789;
    color: #666;
    background-color: rgba(44, 61, 78, 0.8);
}
.sidebar-module ul ul li a:hover {
    background-color: rgba(30, 48, 62, 0.9);
    color: #40bdf7;
}
.sidebar-module.notice {
    background: rgba(30, 48, 62, 0.9);
    border: 1px solid #789;
}
.api-status a {
    color: #40bdf7;
}
.api-status a:hover {
    color: #40bdf7;
}
.api-status .good {
    color: #40bdf7;
}
.content {
    color: #ccc;
}
.content .sectionbody .dlist dt,
.content .verseblock-content,
.content dl code,
.content p > tt,
.content ul code,
.content p code {
    border: 1px solid #789;
    background-color: rgba(30, 48, 62, 0.9);
}
.content table tr {
    border-top: 1px solid #789;
    background-color: rgba(30, 48, 62, 0.9);
}
#markdown-toc li a {
    color: #eee;
    text-decoration: underline;
}
#markdown-toc li a:hover {
    color: #40bdf7;
}
/**************************************************/
/*************** BUTTONS AND LINKS ****************/
/**************************************************/

.branch-name {
    background-color: rgba(30, 48, 62, 0.9);
    border: 1px solid #789;
    color: #fff;
}
a {
    /* color: #70A8F0; */

    color: #40bdf7;
}
a.branch-name {
    color: #fff;
}
a.text-gray-light,
a.link-gray-dark {
    color: #40bdf7 !important;
}
.btn {
    color: #fff;
    background-color: #eee;
    border: 1px solid #678;
    background: linear-gradient(#456, #234);
    /* box-shadow: 0 0 2px rgba(0, 0, 0, 0.7); */
}
.btn:disabled,
.btn:disabled:hover,
.btn.disabled,
.btn.disabled:hover {
    color: rgba(0, 0, 0, 0.5);
}
.btn:hover,
.btn:active,
.btn.selected,
.btn.zeroclipboard-is-hover,
.btn.zeroclipboard-is-active {
    border-color: #ccc;
    background-color: rgba(44, 61, 78, 0.8);
    background-image: linear-gradient(#678, #345);
    border-color: #678;
    box-shadow: 0px 2px 4px rgba(0, 0, 0, 0.15) inset;
}
.btn-primary {
    color: #fff;
    text-shadow: 0px -1px 0px rgba(0, 0, 0, 0.15);
    background-color: #60B044;
    background-image: linear-gradient(#8ADD6D, #60B044);
    border-color: #5CA941;
}
.btn-primary:hover {
    color: #fff;
    background-color: #569E3D;
    background-image: linear-gradient(#79D858, #569E3D);
    border-color: #4A993E;
}
.btn-link {
    color: #fff;
    background-color: transparent;
    border: 0px none;
    -moz-user-select: none;
}
.muted-link {
    color: #fff !important;
    text-decoration: underline !important;
}
.muted-link:hover {
    color: #40bdf7 !important;
    text-decoration: underline !important;
}
.collection-tools .select-menu-button {
    color: #ccc;
}
.collection-tools .select-menu-button:hover {
    color: #40bdf7;
}
.button,
.button-secondary {
    background-color: #40bdf7;
    color: #fff;
}
.button:hover,
.button-secondary:hover {
    background-color: #f0a6f4;
}
/**************************************************/
/* Lighten fonts */
/**************************************************/

.text-danger {
    color: #FD6C40;
}
.text-error,
.text-failure {
    color: #FF3C00;
}
.blankslate {
    background-color: rgba(30, 48, 62, 0.9);
    border: 1px solid #E5E5E5;
    border-radius: 3px;
    box-shadow: 0px 0px 10px rgba(0, 0, 0, 0.05) inset;
}
a.member-row {
    color: #40bdf7;
}
a.member-row:hover {
